import React, { useState, useEffect } from 'react'
import './App.css';
import { Formik, Form, Field, ErrorMessage} from 'formik'

var JSONdata = []

function App() {

  const url = `Url: ${window.location}`
  const device = `UA: ${window.navigator.userAgent}`
  const [options, setOptions] = useState([]);
  var count = 0

  function getData (){


    fetch('https://uvm.mx/suitev3/get_ofertando_vigente')
          .then(response => response.json())
          .then(data => {
            JSONdata = data
            setOptions([])
            //console.log(JSONdata.message)
          });

  }

  useEffect(() => {

    if(count < 2 ){
      console.log(options, count)
      getData() 
      count = 2;
    }


  }, [options]);


  return (
    <div className="App">
      <header className="App-header">

      </header>
      <center>
       <h1>Registrar aspirante</h1>
       <Formik
          initialValues={{
              nombre: '', apaterno: '',  email: '', celular: '' , 
              urlreferrer: url, dispositivo: device,
              banner: 'israel',
              CID: '2016705784.1697574806',
              verify_token: 'UVM.G0-24',
              marcable: 1,


            }}
          validate={values => {
            const errors = {};
            if (!values.email) {
              errors.email = 'Required';
            } else if (
              !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
            ) {
              errors.email = 'Correo inválido';
            }
            if (!values.celular) {
              errors.celular = 'Required';
            } else if (
              !/^[0-9]{10,}$/i.test(values.celular)
            ) {
              errors.email = 'Teléfono inválido';
            }
            return errors;
          }}
          onSubmit={(values, { setSubmitting }) => {
            setTimeout(() => {
              alert(JSON.stringify(values, null, 2));
              setSubmitting(false);
            }, 400);
          }}       
       >
         {({ 
          values,
          setFieldValue,
          errors,
          touched,
          handleChange,
          handleBlur,
          handleSubmit,
          isSubmitting }) => (
           <Form>
             <Field
               type="text"
               name="nombre"
               placeholder="Nombre"
               onChange={handleChange}
               onBlur={handleBlur}
               value={values.nombre}
             />
             <ErrorMessage name="nombre" component="div" />

             <Field
               type="text"
               name="apaterno"
               placeholder="Apellido paterno"
               onChange={handleChange}
               onBlur={handleBlur}
               value={values.apaterno}
             />
             <ErrorMessage name="apaterno" component="div" />

             <Field
               type="email"
               name="email"
               placeholder="Correo electrónico"
               onChange={handleChange}
               onBlur={handleBlur}
               value={values.email}
             /> 
             <ErrorMessage name="email" component="div" />

             <Field
               type="celular"
               name="celular"
               placeholder="Teléfono celular"
             />
             <ErrorMessage name="celular" component="div" />
             <br />

             <select name="selectValue" value={values.selectValue} 
                onChange={(selectedOption) => 
                { setFieldValue("selectValue", selectedOption); }} 
              >
                <option value="">Select the city</option>
                {JSONdata.message ? JSONdata.message.map((obj, key) => (
                  <option key={obj.rs_id} value={obj.ofertando_crmit_name}>
                    {obj.ofertando_crmit_name}
                  </option>
                )): null}
              </select>


             <br />
             <button type="submit" disabled={isSubmitting}>
               Enviar 
             </button>
           </Form>
         )}
       </Formik>
     </center>
    </div>
  );
}

export default App;
