import logo from './logo.svg';
import './App.css';
import { Formik, Form, Field, ErrorMessage} from 'formik'

const url = `Url: ${this.document.location.href}`
const device = `UA: ${window.navigator.userAgent}`
console.log(`Url: ${this.document.location.href}`); 

function App() {
  return (
    <div className="App">
      <header className="App-header">

      </header>
      <center>
       <h1>Register a new account</h1>
       <Formik
          initialValues={{nombre: '', apaterno: '',  email: '', celular: '' }}
          validate={values => {
            const errors = {};
            if (!values.email) {
              errors.email = 'Required';
            } else if (
              !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)
            ) {
              errors.email = 'Correo inválido';
            }
            if (!values.celular) {
              errors.celular = 'Required';
            } else if (
              !/^[0-9]{10,}$/i.test(values.celular)
            ) {
              errors.email = 'Teléfono inválido';
            }
            return errors;
          }}
          onSubmit={(values, { setSubmitting }) => {
            setTimeout(() => {
              alert(JSON.stringify(values, null, 2));
              setSubmitting(false);
            }, 400);
          }}       
       >
         {({ 
          values,
          errors,
          touched,
          handleChange,
          handleBlur,
          handleSubmit,
          isSubmitting }) => (
           <Form>
             <Field
               type="text"
               name="nombre"
               placeholder="Nombre"
               onChange={handleChange}
               onBlur={handleBlur}
               value={values.nombre}
             />
             <ErrorMessage name="nombre" component="div" />

             <Field
               type="text"
               name="apaterno"
               placeholder="Nombre completo"
               onChange={handleChange}
               onBlur={handleBlur}
               value={values.apaterno}
             />
             <ErrorMessage name="apaterno" component="div" />

             <Field
               type="email"
               name="email"
               placeholder="correo electrónico"
               onChange={handleChange}
               onBlur={handleBlur}
               value={values.email}
             /> 
             <ErrorMessage name="email" component="div" />

             <Field
               type="celular"
               name="celular"
               placeholder="teléfono celular"
             />
             <ErrorMessage name="celular" component="div" />

             <button type="submit" disabled={isSubmitting}>
               Enviar 
             </button>
           </Form>
         )}
       </Formik>
     </center>
    </div>
  );
}

export default App;
